import Cookies from 'js-cookie';
import { client } from './client';


// url
import { 
  householdIndex,
 } from '../urls';

// 利用履歴一覧の取得
export const getAllSpending = (userId: number, householdId: number) => {
  if (!Cookies.get("_access_token") || !Cookies.get("_client") || !Cookies.get("_uid") ) return;
  return client.get(`${householdIndex(userId)}/${householdId}/spendings`, { 
      headers: {
        "access-token": Cookies.get("_access_token") || "",
        "client": Cookies.get("_client") || "",
        "uid": Cookies.get("_uid") || "",
      },
    });
};

export const createHousehold = (id: number, params: Household) => {
  if (!Cookies.get("_access_token") || !Cookies.get("_client") || !Cookies.get("_uid") ) return;
  return client.post(`${householdIndex(id)}`, params, {
    headers: {
      "access-token": Cookies.get("_access_token") || "",
      "client": Cookies.get("_client") || "",
      "uid": Cookies.get("_uid") || "",
    },
  });
};
