import React, { useState, useEffect, useContext } from 'react';
import axios from 'axios';
import Cookies from 'js-cookie';
import { AuthContext } from '../../../App';

// date-fns
import format from 'date-fns/format';
import getDate from 'date-fns/getDate';
import getDay from 'date-fns/getDay';
import eachDayOfInterval from 'date-fns/eachDayOfInterval';
import endOfWeek from 'date-fns/endOfWeek';
import eachWeekOfInterval from 'date-fns/eachWeekOfInterval';
import addMonths from 'date-fns/addMonths';
import subMonths from 'date-fns/subMonths';
import startOfMonth from 'date-fns/startOfMonth';
import endOfMonth from 'date-fns/endOfMonth';
import addDays from 'date-fns/addDays';
import { parse } from 'date-fns';

// css
import style from './Calendar.module.scss';

type Props = {
  targetDate: Date;
  setTargetDate: React.Dispatch<React.SetStateAction<Date>>;
  calendar: Date[][];
  spendings: Spending[];
};

interface Spending {
  amountUsed: number;
  createdAt: Date;
  householdId: number;
  id: number;
  memo: string;
  updatedAt: Date;
  usedAt: string;
};

const Calendar: React.FC<Props> = (props) => {
  const {
    setTargetDate,
    calendar,
    spendings,
  } = props;

  const handleSpending = () => {
    console.log(spendings);
    console.log(calendar);

  }

  return (
    <div className={style.calendar}>
      <button onClick={handleSpending}>test</button>
      <div className={style.select_month}>
        <div 
          className={style.selectbutton_month} 
          onClick={() => setTargetDate(current => subMonths(current, 1))}
        >
          &lsaquo; 前の月
        </div>
        <div 
          className={style.selectbutton_month} 
          onClick={() => setTargetDate(new Date())}
        >
          今月
        </div>
        <div 
          className={style.selectbutton_month} 
          onClick={() => setTargetDate(current => addMonths(current, 1))}
        >
          次の月 &rsaquo;
        </div>
      </div>
      <table className={style.calendar_body}>
        <thead>
          <tr className={style.calendar_day}>
            <th>日</th>
            <th>月</th>
            <th>火</th>
            <th>水</th>
            <th>木</th>
            <th>金</th>
            <th>土</th>
          </tr>
        </thead>
        <tbody className={style.calendar_main}>
          {
            calendar.map((weekRow, rowNum) => (
              <tr className={style.calendar_week} key={rowNum}>
                {
                  weekRow.map((date) => (
                    <td 
                      key={getDay(date)}
                      className={style.calendar_date}
                    >
                      <div 
                        className={`
                          ${style.date}
                          ${
                            getDay(date) === 0 ? style.sunday : '' || 
                            getDay(date) === 6 ? style.saturday : '' 
                          }`
                      }>
                        {getDate(date)}
                      </div>
                      <div className={style.date_amount}>
                        {
                          spendings.fileter((val: []) => {
                            const usedDay = getDate(parse(val.usedAt, 'yyyy-mm-dd', new Date()));
                            if(usedDay === getDate(date)) return val
                          }).map((spending) => (
                            <div key={spending.id}>
                              {getDate(parse(spending.usedAt, 'yyyy-mm-dd', new Date()))}
                            </div>
                          ))
                        }
                      </div>
                    </td>
                  ))
                }
              </tr>
            ))
          }
        </tbody>
      </table>
    </div>
  )
}

export default Calendar
