                placeholder='例: 電車代'
                value={newHousehold.name}
                onChange={handleInputChange('name')}
              />
            </div>
            <hr />
            <div className={style.input}>
              <div className={style.inputname}>
                <img src={Date} alt='input reference' />
                <h4>基準日</h4>
              </div>
              <input 
                type='number'
                name='名前'
                min='1'
