import axios from 'axios';
import Cookies from 'js-cookie';
import React, { useContext, useState, useEffect } from 'react'
import { AuthContext } from '../../App';

// url
import { 
  indexUser,
  indexUserId,
  householdIndex,
  householdIndexId,
} from '../../urls';

// interface
import { User } from '../../interface';

// image
import HouseholdBook from '../../image/householdBook.png';

interface Household {
  id: number;
  household_id: string;
  name: string;
  refernce_at: number;
  user_id: number;
  created_at: Date;
  updated_at: Date;
}

const HouseHold: React.FC = () => {
  const { currentUser } = useContext(AuthContext);
  const [households, setHouseholds] = useState([]);


  const handleGetUser = () => {
    if (currentUser) {
      axios.get(`${indexUserId(currentUser.id)}`, { 
          headers: {
            "access-token": Cookies.get("_access_token") || "",
            "client": Cookies.get("_client") || "",
            "uid": Cookies.get("_uid") || "",
          },
        })
        .then((res) => {
          console.log(res.data);
        });
    };
  };

  const handleGetHousehold = (id: any) => {
    if (currentUser) {
      axios.get(`${householdIndex(currentUser.id)}/${id}`,{ 
        headers: {
          "access-token": Cookies.get("_access_token") || "",
          "client": Cookies.get("_client") || "",
          "uid": Cookies.get("_uid") || "",
        },
      })
        .then((res) => {
          console.log(res.data);
        })
    }
  }

  useEffect(() => {
    if (currentUser) {
      axios.get(householdIndex(currentUser.id),{ 
        headers: {
          "access-token": Cookies.get("_access_token") || "",
          "client": Cookies.get("_client") || "",
          "uid": Cookies.get("_uid") || "",
        },
      })
        .then((res) => {
          console.log(res.data);
          setHouseholds(res.data);
        })
        .catch((e) => {
          console.log(e);
        })
    };
  }, [])

  return (
    <div className=''>
      <h2>ユーザー名</h2>
      <h3>3月: ¥100,000</h3>
      <div>
        <p>&lsaquo; 前の月</p>
        <p>次の月 &rsaquo;</p>
      </div>

      {
        households.map((household: Household) => (
          <div key={household.id}>
            {household.name}
            <img src={HouseholdBook} alt='household book' />
            <p>¥10,000</p>
            <button onClick={() => handleGetHousehold(household.id)}>送信</button>
          </div>
        ))
      }
      <button>あたらしい家計簿を作る</button>
    </div>
  )
}

export default HouseHold
