import React, { useState, useEffect, useContext} from 'react';
import { Link, useHistory, useParams } from 'react-router-dom';
import { AuthContext } from '../../../App';

// function
import { useWindowDimensions } from '../../../function/window';

// css
import style from './AddSpending.module.scss';

// interface
import { Spending } from '../../../interface';

// image
  import MemoMark from '../../../image/nameMark.png';
  import Date from '../../../image/calendar.png';
  import PriceMark from '../../../image/priceMark.png';

const AddSpending: React.FC = () => {
  const history = useHistory();
  // const {
  //   setOpenPcHouseholdModal,
  // } = props;
  const { currentUser } = useContext(AuthContext);
  const width = useWindowDimensions();

  const initialSpending = {
    memo: "",
    amount: 0,
    amoutAt: null,
  };
  const [newSpending, setNewSpending] = useState(initialSpending);

  // 入力値の取得
  const handleInputChange = (input: string) => (e: React.ChangeEvent<HTMLInputElement>) => {
    const target = e.target;
    setNewSpending({ ...newSpending, [input]: target.value});
  };

  // 家計簿の新規作成
  // const handleCreateHousehold = async (e: React.MouseEvent<HTMLButtonElement>) => {
  //   e.preventDefault();

  //   const params: Household ={
  //     name: newHousehold.name,
  //     referenceAt: newHousehold.referenceAt,
  //   }
  //   console.log(params);
  //   if(!currentUser) return;

  //   try {
  //     const res = await createHousehold(currentUser.id, params);
  //     if (res?.status === 200) {
  //       console.log(res);
  //       if(width >= 1100) window.location.reload();
  //       history.push('/');
  //     }
  //   } catch (err :any) {
  //     console.log(err)
  //   };

  //   if (width >= 1100) setOpenPcHouseholdModal(false);
  // };

  // // 家計簿新規作成モーダルの表示判定
  // const handleChangeOpenPcHouseholdModal = () => {
  //   setOpenPcHouseholdModal(false);
  // };

  return (
    <>
      <div className={style.addspending}>
        <form>
          <div className={style.inputform}>
            <div className={style.input}>
              <div className={style.inputname}>
                <img src={Date} alt='input reference' />
                <h4>日付</h4>
              </div>
              <input 
                type='date'
                name='date'
                min='1'
                max='31'
                required
                value={newSpending.amount}
                onChange={handleInputChange('amoutAt')}
              />
            </div>
            <hr />
            <div className={style.input}>
              <div className={style.inputname}>
                <img src={PriceMark} alt='input reference' />
                <h4>金額</h4>
              </div>
              <input 
                type='number'
                name='amount'
                min='0'
                required
                value={newSpending.amount}
                onChange={handleInputChange('amout')}
              />
            </div>
            <hr />
            <div className={style.input}>
              <div className={style.inputname}>
                <img src={MemoMark} alt='input memo' />
                <h4>名前</h4>
              </div>
              <input 
                type='text'
                name='memo'
                required
                placeholder='例: 電車代'
                value={newSpending.memo}
                onChange={handleInputChange('memo')}
              />
            </div>
          </div>
          <div className={style.buttonform}>
            <button className={style.saveButton}>
              保存
            </button>
            {width < 1100 ? (
              <Link to='/' className={style.cancelButton}>
                キャンセル
              </Link>
            ) : (
              <div  className={style.cancelButton}>
                キャンセル
              </div>
            ) }
          </div>
        </form>
      </div>
      <div className={style.pcmodal_back}></div>
    </>
  )
}

export default AddSpending
